import React, { useState } from "react";



function CountButton() {

    const [count, setCount ] = useState(0)


    function Increase() {
        setCount( count + 1)
    }

    function Decrease() {
        setCount( count - 1)
    }

    return (
        <div>
            <button onClick={Increase}>+1 </button>
            <span>{count}</span>
            <button onClick={Decrease}> -1</button>
        </div>
    )
}

export default CountButton;
